
public class 변수설정 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
			국어점수, 영어점수, 수학점수
				90		85		80
		 */
		int kor=90;
		int eng=85;
		int math=80;
		
		// 점수출력
		System.out.println("국/영/수:"+kor+"/"+eng+"/"+math); // + 의 기능 : 1) 문자열 결합 , 2) 산술연산
		
		
		

	}

}
